import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule, ActivatedRoute } from '@angular/router';
import { ReactiveFormsModule, FormBuilder, FormGroup } from '@angular/forms';
import { Observable, combineLatest } from 'rxjs';
import { map, startWith, switchMap } from 'rxjs/operators';

import { NewsService } from '../../../core/services/news.service';
import { News, NewsFilter } from '../../../core/models/news.model';
import { LoadingComponent } from '../../../shared/components/loading/loading.component';

@Component({
  selector: 'app-news-list',
  standalone: true,
  imports: [CommonModule, RouterModule, ReactiveFormsModule, LoadingComponent],
  template: `
    <div class="container-fluid px-4 py-4 fade-in">
      <div class="row">
        <div class="col-12">
          <div class="d-flex justify-content-between align-items-center mb-4">
            <h1 class="h3 mb-0">
              <i class="fas fa-newspaper me-2"></i>
              {{ showArchived ? 'Archived' : 'All' }} News
            </h1>
            <button class="btn btn-primary" routerLink="/news/create">
              <i class="fas fa-plus me-2"></i>
              Add News
            </button>
          </div>
        </div>
      </div>

      <!-- Search and Filter -->
      <div class="search-container">
        <form [formGroup]="filterForm" class="row g-3">
          <div class="col-md-4">
            <label for="searchTerm" class="form-label">Search</label>
            <input 
              type="text" 
              class="form-control" 
              id="searchTerm"
              formControlName="searchTerm"
              placeholder="Search news...">
          </div>
          <div class="col-md-3">
            <label for="tags" class="form-label">Tags</label>
            <select class="form-select" id="tags" formControlName="tags" multiple>
              <option *ngFor="let tag of availableTags$ | async" [value]="tag">
                {{ tag }}
              </option>
            </select>
          </div>
          <div class="col-md-2">
            <label for="status" class="form-label">Status</label>
            <select class="form-select" id="status" formControlName="status">
              <option value="">All</option>
              <option value="published">Published</option>
              <option value="archived">Archived</option>
            </select>
          </div>
          <div class="col-md-3 d-flex align-items-end">
            <button type="button" class="btn btn-outline-secondary me-2" (click)="resetFilters()">
              <i class="fas fa-undo me-1"></i>
              Reset
            </button>
            <button type="button" class="btn btn-outline-primary" (click)="toggleArchived()">
              <i class="fas fa-archive me-1"></i>
              {{ showArchived ? 'Show Published' : 'Show Archived' }}
            </button>
          </div>
        </form>
      </div>

      <!-- News List -->
      <div *ngIf="isLoading$ | async">
        <app-loading [height]="300" message="Loading news..."></app-loading>
      </div>

      <div *ngIf="!(isLoading$ | async)">
        <div class="row">
          <div class="col-12" *ngIf="(filteredNews$ | async)?.length === 0">
            <div class="card">
              <div class="card-body text-center py-5">
                <i class="fas fa-inbox fa-3x text-muted mb-3"></i>
                <h5 class="text-muted">No news found</h5>
                <p class="text-muted">
                  {{ showArchived ? 'No archived news available.' : 'No news articles match your criteria.' }}
                </p>
                <button class="btn btn-primary" routerLink="/news/create">
                  <i class="fas fa-plus me-2"></i>
                  Add First News
                </button>
              </div>
            </div>
          </div>

          <div class="col-12" *ngFor="let news of filteredNews$ | async">
            <div class="news-card card mb-3">
              <div class="card-body">
                <div class="row">
                  <div class="col-md-8">
                    <h5 class="card-title">
                      <a [routerLink]="['/news', news.id]" class="text-decoration-none">
                        {{ news.title }}
                      </a>
                      <span *ngIf="news.isArchived" class="badge bg-secondary ms-2">Archived</span>
                    </h5>
                    <p class="card-text">{{ news.summary }}</p>
                    <div class="news-meta">
                      <i class="fas fa-user me-1"></i>
                      {{ news.author }}
