import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { UserService } from '../../core/services/user.service';
import { User } from '../../core/models/user.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-user-add',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  template: `
    <div class="card mb-4">
      <div class="card-header">
        <h5 class="mb-0"><i class="fas fa-user-plus me-2"></i> Add User</h5>
      </div>
      <div class="card-body">
        <form [formGroup]="userForm" (ngSubmit)="onSubmit()" novalidate>
          <div class="row g-3">
            <div class="col-md-6">
              <label class="form-label">Name</label>
              <input type="text" class="form-control" formControlName="name" required>
              <div *ngIf="userForm.get('name')?.invalid && userForm.get('name')?.touched" class="text-danger small">Name is required.</div>
            </div>
            <div class="col-md-6">
              <label class="form-label">Email</label>
              <input type="email" class="form-control" formControlName="email" required>
              <div *ngIf="userForm.get('email')?.invalid && userForm.get('email')?.touched" class="text-danger small">Valid email is required.</div>
            </div>
            <div class="col-md-6">
              <label class="form-label">Password</label>
              <input type="password" class="form-control" formControlName="password" required minlength="6">
              <div *ngIf="userForm.get('password')?.invalid && userForm.get('password')?.touched" class="text-danger small">Password (min 6 chars) is required.</div>
            </div>
            <div class="col-md-6">
              <label class="form-label">Roles</label>
              <select class="form-select" formControlName="roles" multiple required>
                <option *ngFor="let role of availableRoles$ | async" [value]="role">{{ role | titlecase }}</option>
              </select>
              <div *ngIf="userForm.get('roles')?.invalid && userForm.get('roles')?.touched" class="text-danger small">At least one role is required.</div>
            </div>
            <div class="col-md-6">
              <label class="form-label">Profile Image</label>
              <input type="file" class="form-control" (change)="onFileChange($event)">
              <div *ngIf="imagePreview" class="mt-2">
                <img [src]="imagePreview" alt="Preview" class="rounded" style="max-width: 120px; max-height: 120px;">
              </div>
            </div>
          </div>
          <div class="mt-4">
            <button class="btn btn-primary" type="submit" [disabled]="userForm.invalid || loading">
              <i class="fas fa-save me-2"></i> Add User
            </button>
            <button class="btn btn-secondary ms-2" type="button" (click)="router.navigate(['../'])">Cancel</button>
          </div>
        </form>
      </div>
    </div>
  `
})
export class UserAddComponent {
  userForm: FormGroup;
  imagePreview: string | null = null;
  loading = false;
  availableRoles$: Observable<string[]>;

  constructor(
    private fb: FormBuilder,
    public router: Router,
    private userService: UserService
  ) {
    this.userForm = this.fb.group({
      name: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      roles: [[], Validators.required],
      image: ['']
    });
    this.availableRoles$ = this.userService.getAvailableRoles();
  }

  onFileChange(event: Event) {
    const input = event.target as HTMLInputElement;
    if (input.files && input.files.length) {
      const file = input.files[0];
      const reader = new FileReader();
      reader.onload = () => {
        this.imagePreview = reader.result as string;
        this.userForm.patchValue({ image: this.imagePreview });
      };
      reader.readAsDataURL(file);
    }
  }

  onSubmit() {
    if (this.userForm.invalid) return;
    this.loading = true;
    this.userService.createUser(this.userForm.value).subscribe({
      next: () => {
        this.loading = false;
        this.router.navigate(['../']);
      },
      error: () => {
        this.loading = false;
        // handle error (show toast/snackbar if desired)
      }
    });
  }
}
