import { Component, Input, Output, EventEmitter } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';

export interface UserCardData {
  id: string;
  name: string;
  email: string;
  image?: string;
  roles: string[];
  isActive: boolean;
  createdAt: Date;
  [key: string]: any; // Allow additional properties
}

export interface UserCardAction {
  id: string;
  label: string;
  icon: string;
  type: 'view' | 'edit' | 'toggle' | 'delete' | 'custom';
  route?: string;
  disabled?: boolean;
  permission?: string | string[];
  color?: string;
  customAction?: () => void;
}

export interface UserCardConfig {
  data: UserCardData;
  actions: UserCardAction[];
  showAvatar?: boolean;
  showStatus?: boolean;
  showRoles?: boolean;
  showMeta?: boolean;
  showActions?: boolean;
  userRoles?: string[];
  avatarSize?: 'sm' | 'md' | 'lg';
  cardStyle?: 'default' | 'compact' | 'detailed';
  theme?: 'light' | 'dark' | 'auto';
}

@Component({
  selector: 'app-user-card',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './user-card.component.html',
  styleUrls: ['./user-card.component.scss']
})
export class UserCardComponent {
  @Input() config: UserCardConfig = {
    data: {
      id: '',
      name: '',
      email: '',
      roles: [],
      isActive: false,
      createdAt: new Date()
    },
    actions: [],
    showAvatar: true,
    showStatus: true,
    showRoles: true,
    showMeta: true,
    showActions: true,
    userRoles: [],
    avatarSize: 'md',
    cardStyle: 'default',
    theme: 'auto'
  };

  @Output() actionClick = new EventEmitter<{ action: UserCardAction; user: UserCardData }>();
  @Output() statusToggle = new EventEmitter<UserCardData>();
  @Output() userEdit = new EventEmitter<UserCardData>();
  @Output() userDelete = new EventEmitter<UserCardData>();
  @Output() userView = new EventEmitter<UserCardData>();

  hasPermission(action: UserCardAction): boolean {
    if (!action.permission) return true;
    
    const roles = this.config.userRoles || [];
    if (Array.isArray(action.permission)) {
      return action.permission.some(role => roles.includes(role));
    }
    return roles.includes(action.permission);
  }

  handleActionClick(action: UserCardAction) {
    if (action.disabled) return;

    switch (action.type) {
      case 'view':
        this.userView.emit(this.config.data);
        break;
      case 'edit':
        this.userEdit.emit(this.config.data);
        break;
      case 'toggle':
        this.statusToggle.emit(this.config.data);
        break;
      case 'delete':
        this.userDelete.emit(this.config.data);
        break;
      case 'custom':
        if (action.customAction) {
          action.customAction();
        }
        break;
    }

    this.actionClick.emit({ action, user: this.config.data });
  }

  getRoleColor(role: string): string {
    switch (role.toLowerCase()) {
      case 'admin':
        return '#ef4444';
      case 'editor':
        return '#3b82f6';
      case 'author':
        return '#10b981';
      default:
        return '#6b7280';
    }
  }

  getDefaultAvatar(): string {
    return 'https://images.pexels.com/photos/3763188/pexels-photo-3763188.jpeg?auto=compress&cs=tinysrgb&w=100&h=100&dpr=2';
  }

  // Helper method to create a default user card configuration
  static createDefaultConfig(user: UserCardData, userRoles: string[] = []): UserCardConfig {
    return {
      data: user,
      actions: [
        {
          id: 'view',
          label: 'View Profile',
          icon: 'fa-eye',
          type: 'view',
          disabled: true
        },
        {
          id: 'edit',
          label: 'Edit User',
          icon: 'fa-edit',
          type: 'edit',
          route: `./${user.id}/edit`,
          permission: ['admin', 'editor']
        },
        {
          id: 'toggle',
          label: user.isActive ? 'Deactivate' : 'Activate',
          icon: user.isActive ? 'fa-user-times' : 'fa-user-check',
          type: 'toggle',
          color: user.isActive ? 'warning' : 'success',
          permission: ['admin', 'editor']
        },
        {
          id: 'delete',
          label: 'Delete User',
          icon: 'fa-trash',
          type: 'delete',
          color: 'danger',
          permission: ['admin']
        }
      ],
      showAvatar: true,
      showStatus: true,
      showRoles: true,
      showMeta: true,
      showActions: true,
      userRoles,
      avatarSize: 'md',
      cardStyle: 'default',
      theme: 'auto'
    };
  }

  // Helper method to create a compact user card configuration
  static createCompactConfig(user: UserCardData, userRoles: string[] = []): UserCardConfig {
    return {
      data: user,
      actions: [
        {
          id: 'edit',
          label: 'Edit',
          icon: 'fa-edit',
          type: 'edit',
          route: `./${user.id}/edit`,
          permission: ['admin', 'editor']
        },
        {
          id: 'delete',
          label: 'Delete',
          icon: 'fa-trash',
          type: 'delete',
          color: 'danger',
          permission: ['admin']
        }
      ],
      showAvatar: true,
      showStatus: true,
      showRoles: false,
      showMeta: false,
      showActions: true,
      userRoles,
      avatarSize: 'sm',
      cardStyle: 'compact',
      theme: 'auto'
    };
  }
}
