import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule, ActivatedRoute } from '@angular/router';
import { Observable, switchMap } from 'rxjs';

import { NewsService } from '../../../core/services/news.service';
import { News } from '../../../core/models/news.model';
import { LoadingComponent } from '../../../shared/components/loading/loading.component';

@Component({
  selector: 'app-news-detail',
  standalone: true,
  imports: [CommonModule, RouterModule, LoadingComponent],
  template: `
    <div class="container-fluid px-4 py-4 fade-in">
      <div *ngIf="isLoading$ | async">
        <app-loading [height]="400" message="Loading news article..."></app-loading>
      </div>

      <div *ngIf="!(isLoading$ | async) && (news$ | async) as news">
        <!-- Breadcrumb -->
        <nav aria-label="breadcrumb" class="mb-4">
          <ol class="breadcrumb">
            <li class="breadcrumb-item">
              <a routerLink="/">Dashboard</a>
            </li>
            <li class="breadcrumb-item">
              <a routerLink="/news">News</a>
            </li>
            <li class="breadcrumb-item active">{{ news.title }}</li>
          </ol>
        </nav>

        <!-- Article Header -->
        <div class="card mb-4">
          <div class="card-body">
            <div class="row">
              <div class="col-md-8">
                <h1 class="display-6 mb-3">{{ news.title }}</h1>
                <div class="news-meta mb-3">
                  <span class="me-4">
                    <i class="fas fa-user me-2"></i>
                    {{ news.author }}
                  </span>
                  <span class="me-4">
                    <i class="fas fa-calendar me-2"></i>
                    {{ news.publishDate | date:'full' }}
                  </span>
                  <span *ngIf="news.isArchived" class="badge bg-secondary">
                    <i class="fas fa-archive me-1"></i>
                    Archived
                  </span>
                </div>
                <div class="mb-3">
                  <span *ngFor="let tag of news.tags" class="tag me-2">
                    {{ tag }}
                  </span>
                </div>
              </div>
              <div class="col-md-4 text-md-end">
                <div class="d-flex flex-column gap-2">
                  <button 
                    class="btn btn-primary"
                    [routerLink]="['/news', news.id, 'edit']">
                    <i class="fas fa-edit me-2"></i>
                    Edit Article
                  </button>
                  <button 
                    class="btn"
                    [class.btn-success]="news.isArchived"
                    [class.btn-warning]="!news.isArchived"
                    (click)="toggleArchive(news)">
                    <i class="fas" 
                       [class.fa-box-open]="news.isArchived"
                       [class.fa-archive]="!news.isArchived"
                       [class.me-2]="true"></i>
                    {{ news.isArchived ? 'Restore Article' : 'Archive Article' }}
                  </button>
                  <button class="btn btn-outline-secondary" routerLink="/news">
                    <i class="fas fa-arrow-left me-2"></i>
                    Back to News
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        <!-- Article Content -->
        <div class="card">
          <div class="card-body">
            <div class="row">
              <div class="col-lg-8 mx-auto">
                <div class="article-content">
                  <div class="lead mb-4">{{ news.summary }}</div>
                  <div class="content-body">{{ news.content }}</div>
                </div>
              </div>
            </div>
          </div>
        </div>

        <!-- Article Footer -->
        <div class="card mt-4">
          <div class="card-body">
            <div class="row">
              <div class="col-md-6">
                <h6 class="mb-2">Article Information</h6>
                <small class="text-muted">
                  Created: {{ news.createdAt | date:'medium' }}<br>
                  Last Updated: {{ news.updatedAt | date:'medium' }}
                </small>
              </div>
              <div class="col-md-6 text-md-end">
                <div class="d-flex gap-2 justify-content-md-end">
                  <button class="btn btn-sm btn-outline-primary" (click)="shareArticle(news)">
                    <i class="fas fa-share me-1"></i>
                    Share
                  </button>
                  <button class="btn btn-sm btn-outline-secondary" (click)="printArticle()">
                    <i class="fas fa-print me-1"></i>
                    Print
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <!-- Article Not Found -->
      <div *ngIf="!(isLoading$ | async) && !(news$ | async)" class="card">
        <div class="card-body text-center py-5">
          <i class="fas fa-exclamation-triangle fa-3x text-warning mb-3"></i>
          <h5>Article Not Found</h5>
          <p class="text-muted">The requested news article could not be found.</p>
          <button class="btn btn-primary" routerLink="/news">
            <i class="fas fa-arrow-left me-2"></i>
            Back to News
          </button>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .article-content {
      font-size: 1.1rem;
      line-height: 1.7;
    }

    .content-body {
      white-space: pre-wrap;
      word-wrap: break-word;
    }

    .lead {
      font-size: 1.25rem;
      font-weight: 300;
      color: var(--text-secondary);
    }
  `]
})
export class NewsDetailComponent implements OnInit {
  news$: Observable<News | undefined>;
  isLoading$: Observable<boolean>;

  constructor(
    private route: ActivatedRoute,
    private newsService: NewsService
  ) {
    this.isLoading$ = this.newsService.isLoading();

    this.news$ = this.route.params.pipe(
      switchMap(params => this.newsService.getById(params['id']))
    );
  }

  ngOnInit() {}

  toggleArchive(news: News): void {
    if (news.isArchived) {
      this.newsService.unarchive(news.id).subscribe();
    } else {
      this.newsService.archive(news.id).subscribe();
    }
  }

  shareArticle(news: News): void {
    if (navigator.share) {
      navigator.share({
        title: news.title,
        text: news.summary,
        url: window.location.href
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      alert('Article link copied to clipboard!');
    }
  }

  printArticle(): void {
    window.print();
  }
}
