import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { Observable } from 'rxjs';
import { BaseChartDirective } from 'ng2-charts';
import { Chart, ChartConfiguration, ChartData, ChartType } from 'chart.js';
import { ReactiveFormsModule, FormBuilder, FormGroup } from '@angular/forms';
import { map, startWith, switchMap } from 'rxjs/operators';

import { NewsService } from '../../core/services/news.service';
import { UserService } from '../../core/services/user.service';
import { News, NewsStatistics } from '../../core/models/news.model';
import { User } from '../../core/models/user.model';
import { LoadingComponent } from '../../shared/components/loading/loading.component';

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule, RouterModule, LoadingComponent, BaseChartDirective, ReactiveFormsModule],
  template: `
    <div class="container-fluid px-4 py-4 fade-in">
      <div class="row">
        <div class="col-12">
          <div class="d-flex justify-content-between align-items-center mb-4">
            <h1 class="h3 mb-0">
              <i class="fas fa-tachometer-alt me-2"></i>
              Dashboard
            </h1>
            <button class="btn btn-primary" routerLink="/news/create">
              <i class="fas fa-plus me-2"></i>
              Add News
            </button>
          </div>
        </div>
      </div>

      <!-- Dashboard Filter/Search -->
      <div class="search-container mb-4">
        <form [formGroup]="filterForm" class="row g-3 align-items-end">
          <div class="col-md-4">
            <label for="searchTerm" class="form-label">Search News</label>
            <input type="text" class="form-control" id="searchTerm" formControlName="searchTerm" placeholder="Search news...">
          </div>
          <div class="col-md-3">
            <label for="tag" class="form-label">Tag</label>
