import { Component, EventEmitter, HostListener, Input, Output, ChangeDetectionStrategy, ElementRef, ViewChild, AfterViewInit } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-modal',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './modal.component.html',
  styleUrls: ['./modal.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ModalComponent implements AfterViewInit {
  @Input() show = false;
  @Input() ariaLabel = 'Modal Dialog';
  @Output() close = new EventEmitter<void>();

  @ViewChild('dialog') dialogRef!: ElementRef<HTMLDivElement>;

  @HostListener('document:keydown.escape', ['$event'])
  onEsc(event: KeyboardEvent) {
    if (this.show) this.close.emit();
  }

  onBackdropClick(event: MouseEvent) {
    // Only close if the backdrop itself was clicked, not the dialog
    if (event.target === event.currentTarget) {
      this.close.emit();
    }
  }

  ngAfterViewInit() {
    // Focus trap: focus the dialog when opened
    if (this.show && this.dialogRef) {
      setTimeout(() => this.dialogRef.nativeElement.focus(), 0);
    }
  }
}
