import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { ReactiveFormsModule, FormBuilder, FormGroup } from '@angular/forms';
import { Observable, combineLatest } from 'rxjs';
import { map, startWith, switchMap } from 'rxjs/operators';

import { UserService } from '../../core/services/user.service';
import { User, UserFilter } from '../../core/models/user.model';
import { LoadingComponent } from '../../shared/components/loading/loading.component';
import { UserCardComponent, UserCardConfig, UserCardData } from '../../shared/components/user-card/user-card.component';
import { AuthService } from '../../core/services/auth.service';

@Component({
  selector: 'app-users',
  standalone: true,
  imports: [CommonModule, RouterModule, ReactiveFormsModule, LoadingComponent, UserCardComponent],
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.scss']
})
export class UsersComponent implements OnInit {
  filterForm: FormGroup;
  isLoading$: Observable<boolean> = new Observable();
  filteredUsers$: Observable<User[]> = new Observable();
  allUsers$: Observable<User[]> = new Observable();
  activeUsersCount$: Observable<number>;
  adminCount$: Observable<number>;
  inactiveCount$: Observable<number>;
  toastMessage: string = '';
  toastTimeout: any;
  toastType: string = '';
  lastDeletedUser: User | null = null;

  constructor(
    private fb: FormBuilder, 
    private userService: UserService,
    private authService: AuthService
  ) {
    this.filterForm = this.fb.group({
      searchTerm: [''],
      role: [''],
      status: ['']
    });
    this.isLoading$ = this.userService.isLoading$;
    this.filteredUsers$ = this.filterForm.valueChanges.pipe(
      startWith(this.filterForm.value),
      switchMap(formValue => this.userService.getFiltered({
        ...formValue,
        isActive: formValue.status === 'active' ? true : formValue.status === 'inactive' ? false : undefined
      }))
    );
    this.allUsers$ = this.userService.getAll();
    this.activeUsersCount$ = this.filteredUsers$.pipe(map(users => users.filter(u => u.isActive).length));
    this.adminCount$ = this.filteredUsers$.pipe(map(users => users.filter(u => u.roles.includes('admin')).length));
    this.inactiveCount$ = this.filteredUsers$.pipe(map(users => users.filter(u => !u.isActive).length));
  }

  ngOnInit(): void {}

  // Convert User to UserCardData
  convertToUserCardData(user: User): UserCardData {
    return {
      id: user.id,
      name: user.name,
      email: user.email,
      image: user.image,
      roles: user.roles,
      isActive: user.isActive,
      createdAt: user.createdAt
    };
  }

  // Create user card configuration
  createUserCardConfig(user: User): UserCardConfig {
    const userRoles = this.authService.getCurrentUser()?.roles || [];

    return UserCardComponent.createDefaultConfig(
      this.convertToUserCardData(user),
      userRoles
    );
  }

  // Handle user card events
  onUserCardActionClick(event: { action: any; user: UserCardData }) {
    const { action, user } = event;

    switch (action.type) {
      case 'toggle':
        this.toggleUserStatus(this.findUserById(user.id));
        break;
      case 'delete':
        this.deleteUser(this.findUserById(user.id));
        break;
      case 'edit':
        // Navigation is handled by routerLink in the card
        break;
      default:
        console.log('Action clicked:', action.type, user);
    }
  }

  onUserStatusToggle(userData: UserCardData) {
    const user = this.findUserById(userData.id);
    if (user) {
      this.toggleUserStatus(user);
    }
  }

  onUserEdit(userData: UserCardData) {
    const user = this.findUserById(userData.id);
    if (user) {
      // Navigation is handled by routerLink in the card
      console.log('Edit user:', user.name);
    }
  }

  onUserDelete(userData: UserCardData) {
    const user = this.findUserById(userData.id);
    if (user) {
      this.deleteUser(user);
    }
  }

  onUserView(userData: UserCardData) {
    const user = this.findUserById(userData.id);
    if (user) {
      console.log('View user:', user.name);
    }
  }

  // Helper method to find user by ID
  private findUserById(id: string): User | null {
    // This is a simplified version - in a real app, you might want to maintain a users map
    // or get the user from the service
    return null; // For now, return null as we'll handle this differently
  }

  toggleUserStatus(user: User) {
    this.userService.toggleUserStatus(user.id).subscribe(
      () => {
        this.toastMessage = `User '${user.name}' has been ${user.isActive ? 'activated' : 'deactivated'} successfully.`;
        this.toastType = 'success';
        if (this.toastTimeout) {
          clearTimeout(this.toastTimeout);
        }
        this.toastTimeout = setTimeout(() => {
          this.toastMessage = '';
        }, 3000);
      },
      (error) => {
        this.toastMessage = `Failed to ${user.isActive ? 'activate' : 'deactivate'} user '${user.name}'. Please try again later.`;
        this.toastType = 'error';
        if (this.toastTimeout) {
          clearTimeout(this.toastTimeout);
        }
        this.toastTimeout = setTimeout(() => {
          this.toastMessage = '';
        }, 3000);
      }
    );
  }

  deleteUser(user: User) {
    if (window.confirm(`Are you sure you want to delete user '${user.name}'? This action cannot be undone.`)) {
      this.userService.deleteUser(user.id).subscribe(
        () => {
          this.toastMessage = `User '${user.name}' has been deleted successfully.`;
          this.toastType = 'success';
          this.lastDeletedUser = user;
          if (this.toastTimeout) {
            clearTimeout(this.toastTimeout);
          }
          this.toastTimeout = setTimeout(() => {
            this.toastMessage = '';
          }, 3000);
        },
        (error) => {
          this.toastMessage = `Failed to delete user '${user.name}'. Please try again later.`;
          this.toastType = 'error';
          if (this.toastTimeout) {
            clearTimeout(this.toastTimeout);
          }
          this.toastTimeout = setTimeout(() => {
            this.toastMessage = '';
          }, 3000);
        }
      );
    }
  }

  showToast(message: string, type: string) {
    this.toastMessage = message;
    this.toastType = type;
    if (this.toastTimeout) {
      clearTimeout(this.toastTimeout);
    }
    this.toastTimeout = setTimeout(() => {
      this.toastMessage = '';
    }, 3000);
  }

  undoDeleteUser() {
    if (this.lastDeletedUser) {
      const user = this.lastDeletedUser;
      this.userService.restoreUser(user);
      this.showToast(`User '${user.name}' has been restored.`, 'undo');
      this.lastDeletedUser = null;
    }
  }
}
