import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from '../../core/services/user.service';
import { UserFormComponent } from './user-form.component';
import { Observable } from 'rxjs';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-user-add',
  standalone: true,
  imports: [CommonModule, UserFormComponent],
  template: `
    <div class="card mb-4">
      <div class="card-header">
        <h5 class="mb-0"><i class="fas fa-user-plus me-2"></i> Add User</h5>
      </div>
      <div class="card-body">
        <app-user-form
          [mode]="'add'"
          [availableRoles$]="availableRoles$"
          (submitUser)="onSubmit($event)"
          (cancel)="onCancel()"
        ></app-user-form>
        <div *ngIf="toastMessage" class="toast-message fade-in mt-3">{{ toastMessage }}</div>
      </div>
    </div>
  `,
  styles: [`
    .toast-message {
      background: #2563eb;
      color: #fff;
      padding: 0.75rem 1.5rem;
      border-radius: 0.5rem;
      box-shadow: 0 2px 12px rgba(0,0,0,0.10);
      font-weight: 500;
      position: relative;
      z-index: 10;
      min-width: 200px;
      text-align: center;
      animation: fadeIn 0.4s;
    }
    .toast-message.error {
      background: #dc2626;
    }
    .fade-in {
      animation: fadeIn 0.4s;
    }
    @keyframes fadeIn {
      from { opacity: 0; }
      to { opacity: 1; }
    }
  `]
})
export class UserAddComponent {
  availableRoles$: Observable<string[]>;
  toastMessage: string | null = null;
  toastTimeout: any;
  constructor(private userService: UserService, private router: Router) {
    this.availableRoles$ = this.userService.getAvailableRoles();
  }

  onSubmit(formValue: any) {
    this.userService.createUser(formValue).subscribe({
      next: () => {
        this.showToast('User added successfully!');
        setTimeout(() => this.router.navigate(['../']), 1200);
      },
      error: () => {
        this.showToast('Failed to add user.', true);
      }
    });
  }

  onCancel() {
    this.router.navigate(['../']);
  }

  showToast(message: string, error = false) {
    this.toastMessage = message;
    if (error) {
      this.toastMessage += '';
    }
    clearTimeout(this.toastTimeout);
    this.toastTimeout = setTimeout(() => {
      this.toastMessage = null;
    }, 2000);
  }
}
