import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { Observable } from 'rxjs';
import { BaseChartDirective } from 'ng2-charts';
import { Chart, ChartConfiguration, ChartData, ChartType } from 'chart.js';
import { FormBuilder, FormGroup } from '@angular/forms';
import { map, startWith, switchMap } from 'rxjs/operators';

import { NewsService } from '../../core/services/news.service';
import { UserService } from '../../core/services/user.service';
import { News, NewsStatistics } from '../../core/models/news.model';
import { User } from '../../core/models/user.model';
import { LoadingComponent } from '../../shared/components/loading/loading.component';

@Component({
  selector: 'app-dashboard',
  standalone: true,
  imports: [CommonModule, RouterModule, LoadingComponent, BaseChartDirective],
  template: `
    <div class="container-fluid px-4 py-4 fade-in">
      <div class="row">
        <div class="col-12">
          <div class="d-flex justify-content-between align-items-center mb-4">
            <h1 class="h3 mb-0">
              <i class="fas fa-tachometer-alt me-2"></i>
              Dashboard
            </h1>
            <button class="btn btn-primary" routerLink="/news/create">
              <i class="fas fa-plus me-2"></i>
              Add News
            </button>
          </div>
        </div>
      </div>

      <!-- Statistics Cards -->
      <div class="row mb-4">
        <div class="col-md-3 col-sm-6 mb-3">
          <div class="card stat-card">
            <div class="card-body">
              <div class="d-flex align-items-center">
                <div class="flex-grow-1">
                  <div class="stat-value">{{ (statistics$ | async)?.totalNews || 0 }}</div>
                  <div class="stat-label">Total News</div>
                </div>
                <div class="ms-3">
                  <i class="fas fa-newspaper fa-2x"></i>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div class="col-md-3 col-sm-6 mb-3">
          <div class="card stat-card">
            <div class="card-body">
              <div class="d-flex align-items-center">
                <div class="flex-grow-1">
                  <div class="stat-value">{{ (statistics$ | async)?.publishedNews || 0 }}</div>
                  <div class="stat-label">Published</div>
                </div>
                <div class="ms-3">
                  <i class="fas fa-eye fa-2x"></i>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div class="col-md-3 col-sm-6 mb-3">
          <div class="card stat-card">
            <div class="card-body">
              <div class="d-flex align-items-center">
                <div class="flex-grow-1">
                  <div class="stat-value">{{ (statistics$ | async)?.archivedNews || 0 }}</div>
                  <div class="stat-label">Archived</div>
                </div>
                <div class="ms-3">
                  <i class="fas fa-archive fa-2x"></i>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div class="col-md-3 col-sm-6 mb-3">
          <div class="card stat-card">
            <div class="card-body">
              <div class="d-flex align-items-center">
                <div class="flex-grow-1">
                  <div class="stat-value">{{ (activeUsers$ | async)?.length || 0 }}</div>
                  <div class="stat-label">Active Users</div>
                </div>
                <div class="ms-3">
                  <i class="fas fa-users fa-2x"></i>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div class="row">
        <!-- Recent News -->
        <div class="col-lg-8 mb-4">
          <div class="card">
            <div class="card-header d-flex justify-content-between align-items-center">
              <h5 class="mb-0">
                <i class="fas fa-newspaper me-2"></i>
                Recent News
              </h5>
              <a routerLink="/news" class="btn btn-sm btn-outline-primary">
                View All
              </a>
            </div>
            <div class="card-body">
              <div *ngIf="isLoading$ | async">
                <app-loading [height]="200" message="Loading recent news..."></app-loading>
              </div>

              <div *ngIf="!(isLoading$ | async)">
                <div *ngFor="let news of recentNews$ | async" class="news-card card mb-3">
                  <div class="card-body">
                    <h6 class="card-title">
                      <a [routerLink]="['/news', news.id]" class="text-decoration-none">
                        {{ news.title }}
                      </a>
                    </h6>
                    <p class="card-text">{{ news.summary }}</p>
                    <div class="d-flex justify-content-between align-items-center">
                      <div class="news-meta">
                        <i class="fas fa-user me-1"></i>
                        {{ news.author }}
                        <i class="fas fa-calendar ms-3 me-1"></i>
                        {{ news.publishDate | date:'mediumDate' }}
                      </div>
                      <div>
                        <span *ngFor="let tag of news.tags.slice(0, 2)" class="tag">
                          {{ tag }}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        <!-- Charts -->
        <div class="col-lg-4 mb-4">
          <div class="card">
            <div class="card-header">
              <h5 class="mb-0">
                <i class="fas fa-chart-pie me-2"></i>
                News by Tag
              </h5>
            </div>
            <div class="card-body">
              <div class="chart-container">
                <canvas 
                  baseChart
                  [data]="tagChartData"
                  [type]="'doughnut'"
                  [options]="chartOptions">
                </canvas>
              </div>
            </div>
          </div>
        </div>
      </div>

      <!-- Monthly News Chart -->
      <div class="row">
        <div class="col-12">
          <div class="card">
            <div class="card-header">
              <h5 class="mb-0">
                <i class="fas fa-chart-line me-2"></i>
                News Published by Month
              </h5>
            </div>
            <div class="card-body">
              <div class="chart-container">
                <canvas 
                  baseChart
                  [data]="monthlyChartData"
                  [type]="'line'"
                  [options]="lineChartOptions">
                </canvas>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: [`
    .card, .stat-card {
      border-radius: 1rem;
      box-shadow: 0 2px 12px rgba(0,0,0,0.06);
      border: 1px solid #e5e7eb;
      margin-bottom: 1.5rem;
      transition: box-shadow 0.2s, border-color 0.2s;
    }
    .card:hover, .stat-card:hover {
      box-shadow: 0 4px 24px rgba(37,99,235,0.10);
      border-color: #2563eb;
    }
    .fade-in {
      animation: fadeIn 0.5s;
    }
    @keyframes fadeIn {
      from { opacity: 0; }
      to { opacity: 1; }
    }
    @media (max-width: 767px) {
      .card, .stat-card {
        margin-bottom: 1rem;
      }
    }
  `]
})
export class DashboardComponent implements OnInit {
  statistics$: Observable<NewsStatistics>;
  activeUsers$: Observable<User[]>;
  isLoading$: Observable<boolean>;
  recentNews$: Observable<News[]>;
  tagChartData: ChartData<'doughnut'>;
  chartOptions: ChartConfiguration['options'];
  monthlyChartData: ChartData<'line'>;
  lineChartOptions: ChartConfiguration['options'];
  filterForm: FormGroup;
  filteredNews$: Observable<News[]>;
  filteredUsers$: Observable<User[]>;

  constructor(
    private newsService: NewsService,
    private userService: UserService,
    private fb: FormBuilder
  ) {
    this.filterForm = this.fb.group({
      searchTerm: [''],
      tag: [''],
      userSearch: ['']
    });
    this.filteredNews$ = this.filterForm.valueChanges.pipe(
      startWith(this.filterForm.value),
      switchMap(formValue => this.newsService.getFilteredNews({
        searchTerm: formValue.searchTerm,
        tags: formValue.tag ? [formValue.tag] : []
      }))
    );
    this.filteredUsers$ = this.filterForm.valueChanges.pipe(
      startWith(this.filterForm.value),
      switchMap(formValue => this.userService.getFiltered({
        searchTerm: formValue.userSearch
      }))
    );
    this.statistics$ = this.filteredNews$.pipe(
      map(news => {
        const totalNews = news.length;
        const publishedNews = news.filter(n => !n.isArchived).length;
        const archivedNews = news.filter(n => n.isArchived).length;
        const newsPerMonth: { [key: string]: number } = {};
        const newsPerTag: { [key: string]: number } = {};
        news.forEach(item => {
          const monthKey = new Date(item.publishDate).toLocaleDateString('en-US', { year: 'numeric', month: 'short' });
          newsPerMonth[monthKey] = (newsPerMonth[monthKey] || 0) + 1;
          item.tags.forEach(tag => {
            newsPerTag[tag] = (newsPerTag[tag] || 0) + 1;
          });
        });
        return { totalNews, publishedNews, archivedNews, newsPerMonth, newsPerTag };
      })
    );
    this.activeUsers$ = this.filteredUsers$.pipe(
      map(users => users.filter(u => u.isActive))
    );
    this.isLoading$ = this.newsService.isLoading$;
    this.recentNews$ = this.filteredNews$.pipe(
      map(news => news.filter(n => !n.isArchived).sort((a, b) => new Date(b.publishDate).getTime() - new Date(a.publishDate).getTime()).slice(0, 5))
    );
    this.tagChartData = { labels: [], datasets: [] };
    this.chartOptions = {};
    this.monthlyChartData = { labels: [], datasets: [] };
    this.lineChartOptions = {};
  }

  ngOnInit(): void {}
}
