import { Component, Input, Output, EventEmitter, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule, AbstractControl, ValidationErrors, AsyncValidatorFn } from '@angular/forms';
import { User } from '../../core/models/user.model';
import { Observable, of } from 'rxjs';
import { map, debounceTime, switchMap, first } from 'rxjs/operators';
import { UserService } from '../../core/services/user.service';

function strongPasswordValidator(control: AbstractControl): ValidationErrors | null {
  const value = control.value || '';
  if (!value) return null;
  const hasNumber = /[0-9]/.test(value);
  const hasLetter = /[a-zA-Z]/.test(value);
  return value.length >= 8 && hasNumber && hasLetter ? null : { strongPassword: true };
}

function uniqueEmailValidator(userService: UserService, currentUserId?: string): AsyncValidatorFn {
  return (control: AbstractControl) => {
    const email = control.value;
    if (!email) return of(null);
    return userService.getAll().pipe(
      debounceTime(300),
      map(users => {
        const found = users.find(u => u.email === email && u.id !== currentUserId);
        return found ? { uniqueEmail: true } : null;
      }),
      first()
    );
  };
}

@Component({
  selector: 'app-user-form',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  template: `
    <form [formGroup]="userForm" (ngSubmit)="onSubmit()" novalidate>
      <div class="row g-3">
        <div class="col-md-6">
          <label class="form-label">Name</label>
          <input type="text" class="form-control" formControlName="name" required>
          <div *ngIf="userForm.get('name')?.invalid && userForm.get('name')?.touched" class="text-danger small fade-in">Name is required.</div>
        </div>
        <div class="col-md-6">
          <label class="form-label">Email</label>
          <input type="email" class="form-control" formControlName="email" required>
          <div *ngIf="userForm.get('email')?.errors?.['required'] && userForm.get('email')?.touched" class="text-danger small fade-in">Email is required.</div>
          <div *ngIf="userForm.get('email')?.errors?.['email'] && userForm.get('email')?.touched" class="text-danger small fade-in">Enter a valid email address.</div>
          <div *ngIf="userForm.get('email')?.errors?.['uniqueEmail'] && userForm.get('email')?.touched" class="text-danger small fade-in">This email is already in use.</div>
        </div>
        <div class="col-md-6" *ngIf="mode === 'add'">
          <label class="form-label">Password</label>
          <input type="password" class="form-control" formControlName="password" required minlength="8">
          <div *ngIf="userForm.get('password')?.errors?.['required'] && userForm.get('password')?.touched" class="text-danger small fade-in">Password is required.</div>
          <div *ngIf="userForm.get('password')?.errors?.['minlength'] && userForm.get('password')?.touched" class="text-danger small fade-in">Password must be at least 8 characters.</div>
          <div *ngIf="userForm.get('password')?.errors?.['strongPassword'] && userForm.get('password')?.touched" class="text-danger small fade-in">Password must contain at least one letter and one number.</div>
        </div>
        <div class="col-md-6">
          <label class="form-label">Roles</label>
          <select class="form-select" formControlName="roles" multiple required>
            <option *ngFor="let role of availableRoles$ | async" [value]="role">{{ role | titlecase }}</option>
          </select>
          <div *ngIf="userForm.get('roles')?.invalid && userForm.get('roles')?.touched" class="text-danger small fade-in">At least one role is required.</div>
        </div>
        <div class="col-md-6">
          <label class="form-label">Profile Image</label>
          <input type="file" class="form-control" (change)="onFileChange($event)">
          <div *ngIf="imagePreview" class="mt-2">
            <img [src]="imagePreview" alt="Preview" class="rounded" style="max-width: 120px; max-height: 120px;">
          </div>
        </div>
      </div>
      <div class="mt-4">
        <button class="btn btn-primary" type="submit" [disabled]="userForm.invalid || loading">
          <i class="fas fa-save me-2"></i> {{ mode === 'edit' ? 'Update User' : 'Add User' }}
        </button>
        <button class="btn btn-secondary ms-2" type="button" (click)="cancel.emit()">Cancel</button>
      </div>
    </form>
  `,
  styles: [`
    .fade-in {
      animation: fadeIn 0.4s;
    }
    @keyframes fadeIn {
      from { opacity: 0; }
      to { opacity: 1; }
    }
  `]
})
export class UserFormComponent implements OnInit {
  @Input() user: User | null = null;
  @Input() mode: 'add' | 'edit' = 'add';
  @Input() availableRoles$: Observable<string[]> = new Observable();
  @Output() submitUser = new EventEmitter<any>();
  @Output() cancel = new EventEmitter<void>();

  userForm: FormGroup;
  imagePreview: string | null = null;
  loading = false;

  constructor(private fb: FormBuilder, private userService: UserService) {
    this.userForm = this.fb.group({
      name: ['', Validators.required],
      email: ['', {
        validators: [Validators.required, Validators.email],
        asyncValidators: [uniqueEmailValidator(this.userService)],
        updateOn: 'blur'
      }],
      password: ['', [Validators.required, Validators.minLength(8), strongPasswordValidator]],
      roles: [[], Validators.required],
      image: ['']
    });
  }

  ngOnInit() {
    if (this.user) {
      this.userForm.patchValue({
        name: this.user.name,
        email: this.user.email,
        roles: this.user.roles,
        image: this.user.image || ''
      });
      this.imagePreview = this.user.image || null;
      if (this.mode === 'edit') {
        this.userForm.get('password')?.clearValidators();
        this.userForm.get('password')?.updateValueAndValidity();
        // Set unique email validator to ignore current user
        this.userForm.get('email')?.setAsyncValidators([uniqueEmailValidator(this.userService, this.user.id)]);
      }
    }
  }

  onFileChange(event: Event) {
    const input = event.target as HTMLInputElement;
    if (input.files && input.files.length) {
      const file = input.files[0];
      const reader = new FileReader();
      reader.onload = () => {
        this.imagePreview = reader.result as string;
        this.userForm.patchValue({ image: this.imagePreview });
      };
      reader.readAsDataURL(file);
    }
  }

  onSubmit() {
    if (this.userForm.invalid) return;
    this.submitUser.emit(this.userForm.value);
  }
}
