import { Component, Input, Output, EventEmitter } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { News } from '../../../core/models/news.model';

export interface DashboardNewsCardAction {
  icon: string;
  tooltip: string;
  type: 'primary' | 'secondary' | 'outline-primary' | 'outline-secondary' | 'success' | 'warning' | 'danger';
  routerLink?: string[];
  callback?: () => void;
  disabled?: boolean;
  showCondition?: boolean;
}

export interface DashboardNewsCardConfig {
  showStatus?: boolean;
  showActions?: boolean;
  showTags?: boolean;
  showMeta?: boolean;
  showFooter?: boolean;
  showReadMore?: boolean;
  customClass?: string;
  maxTags?: number;
  maxSummaryLength?: number;
  variant?: 'default' | 'compact' | 'featured';
}

@Component({
  selector: 'app-dashboard-news-card',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './dashboard-news-card.component.html',
  styleUrls: ['./dashboard-news-card.component.scss']
})
export class DashboardNewsCardComponent {
  @Input() news!: News;
  @Input() actions: DashboardNewsCardAction[] = [];
  @Input() config: DashboardNewsCardConfig = {
    showStatus: true,
    showActions: false,
    showTags: true,
    showMeta: true,
    showFooter: false,
    showReadMore: true,
    maxTags: 3,
    maxSummaryLength: 120,
    variant: 'default'
  };

  @Output() actionClicked = new EventEmitter<{action: string, news: News}>();
  @Output() viewClicked = new EventEmitter<News>();
  @Output() tagClicked = new EventEmitter<string>();

  onActionClick(action: DashboardNewsCardAction) {
    if (action.callback) {
      action.callback();
    } else if (action.routerLink) {
      // Router will handle navigation
    } else {
      this.actionClicked.emit({ action: action.tooltip.toLowerCase().replace(/\s+/g, '_'), news: this.news });
    }
  }

  onViewClick() {
    this.viewClicked.emit(this.news);
  }

  onTagClick(tag: string) {
    this.tagClicked.emit(tag);
  }

  getVisibleTags(): string[] {
    if (!this.config.showTags || !this.news.tags) {
      return [];
    }
    return this.news.tags.slice(0, this.config.maxTags || 3);
  }

  getTruncatedSummary(): string {
    if (!this.config.maxSummaryLength || this.news.summary.length <= this.config.maxSummaryLength) {
      return this.news.summary;
    }
    return this.news.summary.substring(0, this.config.maxSummaryLength) + '...';
  }

  getStatusBadgeClass(): string {
    return this.news.isArchived ? 'status-badge archived' : 'status-badge published';
  }

  getStatusIcon(): string {
    return this.news.isArchived ? 'fa-archive' : 'fa-check-circle';
  }

  getStatusText(): string {
    return this.news.isArchived ? 'Archived' : 'Published';
  }

  getTimeAgo(): string {
    const now = new Date();
    const publishDate = new Date(this.news.publishDate);
    const diffInHours = Math.floor((now.getTime() - publishDate.getTime()) / (1000 * 60 * 60));
    
    if (diffInHours < 1) {
      return 'Just now';
    } else if (diffInHours < 24) {
      return `${diffInHours}h ago`;
    } else if (diffInHours < 168) { // 7 days
      const days = Math.floor(diffInHours / 24);
      return `${days}d ago`;
    } else {
      return this.news.publishDate.toLocaleDateString('en-US', { 
        month: 'short', 
        day: 'numeric' 
      });
    }
  }

  getCardClass(): string {
    const baseClass = 'dashboard-news-card';
    const variantClass = `variant-${this.config.variant || 'default'}`;
    const customClass = this.config.customClass || '';
    
    return `${baseClass} ${variantClass} ${customClass}`.trim();
  }
}
