import { Component, Input, OnInit, HostListener, Output, EventEmitter } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule, Router } from '@angular/router';
import { ThemeService } from '../../../core/services/theme.service';

export interface SidebarItem {
  label: string;
  icon?: string;
  route?: string;
  children?: SidebarItem[];
  permission?: string | string[];
}

@Component({
  selector: 'app-sidebar',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.scss']
})
export class SidebarComponent implements OnInit {
  @Input() items: SidebarItem[] = [];
  @Input() collapsed = false;
  @Input() userRoles: string[] = [];
  @Output() collapsedChange = new EventEmitter<boolean>();

  activeRoute: string = '';
  expandedGroups: Set<string> = new Set();
  isMobile = false;
  isOpen = false;

  constructor(private router: Router, public themeService: ThemeService) {
    this.router.events.subscribe(() => {
      this.activeRoute = this.router.url;
      // Close mobile sidebar when navigating
      if (this.isMobile && this.isOpen) {
        this.closeMobileSidebar();
      }
    });
  }

  ngOnInit() {
    this.activeRoute = this.router.url;
    this.checkScreenSize();
  }

  @HostListener('window:resize')
  onResize() {
    this.checkScreenSize();
  }

  checkScreenSize() {
    this.isMobile = window.innerWidth <= 768;
    if (!this.isMobile && this.isOpen) {
      this.closeMobileSidebar();
    }
  }

  hasPermission(item: SidebarItem): boolean {
    if (!item.permission) return true;
    if (Array.isArray(item.permission)) {
      return item.permission.some(role => this.userRoles.includes(role));
    }
    return this.userRoles.includes(item.permission);
  }

  toggleCollapse() {
    if (this.isMobile) {
      this.toggleMobileSidebar();
    } else {
      this.collapsed = !this.collapsed;
      this.collapsedChange.emit(this.collapsed);
    }
  }

  toggleMobileSidebar() {
    this.isOpen = !this.isOpen;
    if (this.isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }
  }

  closeMobileSidebar() {
    this.isOpen = false;
    document.body.style.overflow = '';
  }

  onBackdropClick() {
    this.closeMobileSidebar();
  }

  onGroupClick(item: SidebarItem) {
    if (item.children) {
      if (this.expandedGroups.has(item.label)) {
        this.expandedGroups.delete(item.label);
      } else {
        this.expandedGroups.add(item.label);
      }
    }
    if (item.route) {
      this.router.navigate([item.route]);
    }
  }

  isGroupActive(item: SidebarItem): boolean {
    if (item.route && this.activeRoute === item.route) return true;
    if (item.children) {
      return item.children.some(child => child.route && this.activeRoute.startsWith(child.route));
    }
    return false;
  }
}
